async function Heiliao() {
    const cheerio = await loadCheerio()
    return new (class {
        constructor() {
            this.load = cheerio.load
            this.navUrl = NAV_URL
        }
        async getNewUrl() {
            console.log(`‚è≥ Ê≠£Âú®Ëé∑ÂèñÊúÄÊñ∞Âú∞ÂùÄ...`)
            try {
                const html = await Request(this.navUrl)
                const Query = this.load(html)
                const linkList = Query('.box-wrap a')
                    .map((i, el) => {
                        const link = Query(el).attr('href')
                        const title = Query(el).find('p').eq(0).text()
                        return { link, title }
                    })
                    .get()
                    .filter((item) => item.title.includes('Á∫øË∑Ø'))
                console.log(`Ëé∑ÂèñÂà∞ÊúÄÊñ∞Âú∞ÂùÄ: ${linkList[0].link}`)
                return linkList[0].link
            } catch (e) {
                throw new Error(`Ëé∑ÂèñÊúÄÊñ∞Âú∞ÂùÄÂ§±Ë¥•: ${e}`)
            }
        }
        async getHotList(url) {
            console.log(`‚è≥ Ê≠£Âú®Ëé∑ÂèñÁÉ≠Èó®ÂàóË°®...`)
            try {
                const html = await Request(url)
                const Query = this.load(html)
                const list = Query('.cursor-pointer')
                    .map((i, el) => {
                        const title = Query(el).find('.title').text()
                        const status = Query(el).find('.ishot').text()
                        const link = url + Query(el).attr('href')
                        return { title, link, status }
                    })
                    .get()
                    .filter((item) => item.title && item.status === 'ÁÉ≠')
                /**
                 * Ëé∑ÂèñÊó∂Èó¥Â∑Æ
                 * @param {*} date YYYY/MM/DD
                 */
                const getDiff = (date) => {
                    const now = new Date().getTime()
                    const target = new Date(date).getTime()
                    const diff = Math.floor((now - target) / 1000 / 60 / 60 / 24)
                    return diff
                }
                const result = await Promise.all(
                    list.map(async (item) => {
                        const date = await this.getPublishDate(item.link)
                        const diff = getDiff(date)
                        return { ...item, date, diff }
                    })
                )
                let minDiff = Infinity // ÂèñÊúÄÂ∞èÂÄº
                const hotList = []
                for (const item of result) {
                    if (item.diff < minDiff) {
                        minDiff = item.diff
                        hotList.length = 0
                    }
                    if (item.diff === minDiff) {
                        hotList.push(item)
                    }
                }
                console.log(`Ëé∑ÂèñÂà∞ÁÉ≠Èó®ÂàóË°®: ${JSON.stringify(hotList, null, 2)}`)
                return hotList
            } catch (e) {
                throw new Error(`Ëé∑ÂèñÁÉ≠Èó®ÂàóË°®Â§±Ë¥•: ${e}`)
            }
        }
        async getPublishDate(url) {
            console.log(`‚è≥ Ê≠£Âú®Ëé∑ÂèñÂèëÂ∏ÉÊó∂Èó¥...`)
            try {
                const html = await Request(url)
                const Query = this.load(html)
                const date = Query('.detail-date .detail-txt').text().split('‚Ä¢')[1].replace(/Âπ¥/, '/').replace(/Êúà/, '/').replace(/Êó•/, '').trim()
                console.log(`Ëé∑ÂèñÂà∞ÂèëÂ∏ÉÊó∂Èó¥: ${date}`)
                return date
            } catch (e) {
                throw new Error(`Ëé∑ÂèñÂèëÂ∏ÉÊó∂Èó¥Â§±Ë¥•: ${e}`)
            }
        }
    })()
}
